(10525, 'L\'Europe en enfer', 'titre_article', 6, '&lt;p class=&quot;TEXTECOURANT&quot; style=&quot;text-align:justify; text-indent:8.5pt&quot;&gt;&lt;b&gt;&lt;span style=&quot;font-size:12.0pt&quot;&gt;&lt;span style=&quot;font-family:DINOT-Bold&quot;&gt;&lt;span style=&quot;letter-spacing:-.3pt&quot;&gt;Ian Kershaw, &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;i&gt;&lt;span style=&quot;font-size:12.0pt&quot;&gt;&lt;span style=&quot;font-family:DINOT-BoldItalic&quot;&gt;&lt;span style=&quot;letter-spacing:-.3pt&quot;&gt;L&amp;rsquo;Europe en enfer (1914-1949), &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/i&gt;&lt;span style=&quot;font-size:12.0pt&quot;&gt;&lt;span style=&quot;font-family:DINOT-Bold&quot;&gt;&lt;span style=&quot;text-transform:uppercase&quot;&gt;&lt;span style=&quot;letter-spacing:-.3pt&quot;&gt;&amp;eacute;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-size:12.0pt&quot;&gt;&lt;span style=&quot;font-family:DINOT-Bold&quot;&gt;&lt;span style=&quot;letter-spacing:-.3pt&quot;&gt;d. du Seuil, 2016, 640 p., 26 &amp;euro;.&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;\r\n;'),
